package com.lonfe.processor;import java.lang.reflect.Method;import java.util.HashMap;import java.util.Map;public class ServiceMeta {	private Map<String, Method> methodMap = new HashMap<String, Method>();	public ServiceMeta(Class<?> servcieClass) {		Method[] methodList = servcieClass.getMethods();		for (int i = 0; i < methodList.length; i++) {			Method method = methodList[i];			methodMap.put(method.getName(), method);		}	}	public Method getMethod(String methodName) {		return methodMap.get(methodName);	}}