package com.lonfe.spring;import com.lonfe.processor.Service;import com.lonfe.processor.ServiceExportor;import org.springframework.beans.factory.FactoryBean;public class ProvidersBean implements FactoryBean<Object> {	private String id;	private String providersInterface;	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getProvidersInterface() {		return providersInterface;	}	public void setProvidersInterface(String providersInterface) {		this.providersInterface = providersInterface;	}	public Object getObject() throws Exception {		ProvidersBean providersBean = new ProvidersBean();		providersBean.setId(id);		providersBean.setProvidersInterface(providersInterface);		Service service = new Service();		service.setName(providersInterface);		ServiceExportor.addToServiceMap(service);		return providersBean;	}	public Class<?> getObjectType() {		return null;	}	public boolean isSingleton() {		return true;	}}