package com.lonfe.spring;import org.springframework.beans.factory.FactoryBean;public class ReferencesProxy implements FactoryBean<Object> {	private String id;	private String serviceInterface;	private String serviceUrl;	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getServiceInterface() {		return serviceInterface;	}	public void setServiceInterface(String serviceInterface) {		this.serviceInterface = serviceInterface;	}	public String getServiceUrl() {		return serviceUrl;	}	public void setServiceUrl(String serviceUrl) {		this.serviceUrl = serviceUrl;	}	public Object getObject() throws Exception {		return RPCProxy.create(Class.forName(serviceInterface), serviceUrl);	}	public Class<?> getObjectType() {		return null;	}	public boolean isSingleton() {		return true;	}}